package com.parallelsymmetry.util;

import junit.framework.TestCase;

import org.junit.Test;

public class Base64Test extends TestCase {

	@Test
	public void testbase64Encode() throws Exception {
		assertEquals( null, Base64.decode( null ) );
		assertEquals( "", Base64.decode( "" ) );
		assertEquals( "asure.", Base64.decode( "YXN1cmUu" ) );
		assertEquals( "easure.", Base64.decode( "ZWFzdXJlLg==" ) );
		assertEquals( "Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure.", Base64.decode( "" ) );
	}

	@Test
	public void testbase64Decode() throws Exception {
		assertEquals( null, Base64.encode( null ) );
		assertEquals( "", Base64.encode( "" ) );
		assertEquals( "YXN1cmUu", Base64.encode( "asure." ) );
		assertEquals( "ZWFzdXJlLg==", Base64.encode( "easure." ) );
		assertEquals( "", Base64.encode( "Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure." ) );
	}

}
